import IAction from '../IAction';
import { utf8to16 } from '../DataFormat';
import IResponse from './issueEInvoice/IResponse';
import base64 from 'base-64';
import request from 'superagent';

//èˆªä¿¡
export default class HangXin implements IAction {

    sendData = (url, data) => {
        const response = request.post(url).type('form').send(data).then(res => {
            const response2: IResponse = utf8to16(base64.decode(res.body));
            console.log(JSON.stringify(response2));
            return response2;
        });
        return response;
    }
    fpkj = (url, data) => {
        return this.sendData(url, data);
    }
    fpdy = (url,data) => {
        this.sendData(url, data);
    }
    fpzf = (url, data) => {
        return this.sendData(url, data);
    }
    fphm = (url) => {
        return this.sendData(url, {});
    }
} 