export default interface IResponse {
    script: null,
    error: number, 
    data: {
        /**
         * 返回代码
         * 0000 成功，其他为错误
         */
        returnCode: string, 
        /**
         * 返回描述
         * 0000 返回成功、其他返回错误描述
         */
        returnMessage: string, 
        /**
         * 返回 base64 数据结果
         * Base64(utf-8) 只返回接口状态，data 无数据项
         */
        data: string
    },
    success: boolean
}