    send = (url, data) => {
        // 真实开票用到的请求，将js对象转换xml字符串
        const reqXml = builder.create(toAt(transformData('jinshui', data))).end({ pretty: true });
        const encodeData: string = base64.encode(utf16to8(reqXml));
        const response = this.Seeya(url, { args: encodeData });
        return response;
    }
    Seeya = (url: string, data: IRequest) => {
        request.post(url).type('form').send(data).then(res => {
            const response: IResponse = utf8to16(base64.decode(res.body));
            console.log(JSON.stringify(response));
            return response;
        }).then(res => {
            return new Promise((resolve, reject) => {
                parseString(
                    res,
                    { explicitArray: false },
                    (err, result: any) => {
                        if (err) {
                            return reject(err);
                        }
                        console.log('result', result);
                        resolve(result);
                    }
                );
            });
        });
    }
