/**
 * 发票打印请求
 * _开头的为行属性,没有_下滑线就是子节点
 */
export default interface IRequest {
    
    business: {
        _comment:string;
        _id:string;
        body:{
            /**
             * 应用类型代码
             * 1：国税 2：地税
             */
            _yylxdm:string;
            input:{
                nsrsbh:string;
                skpbh:string;
                skpkl:string;
                keypwd:string;
                fplxdm:string;
                /**
                 * 开票类型
                 * 0：正数发票开具 1：负数发票开具（红冲）
                 */
                fpdm:string;
                /**
                 * 发票号码 单张打印时为8，批量打印时，起始号码+终止号码=16位
                 */
                fphm:string;
                /**
                 * 打印类型 0：发票打印，1：清单打印 不弹对话框如下 9：发票打印，8：清单打印
                 */
                dylx:string;
                dyfs:string;
                dyjmc:string;
            }
        }
    };
    
}
