
import HangXin from './HangXin';
import JinShui from './JinShui';
import ShuiKong from './ShuiKong';
import JinHang from './JinHang';
import IAction from './IAction';
export default class Engine {
    engine: IAction;

    /**
     * 
     * @param type 选择哪一种方式
     * @param url 接口地址
     * @param data 传输的数据
     */
    constructor(type: string) {
        const lowercase = type.toLowerCase();
        if (lowercase === "hangxin") {
            this.engine = new HangXin();
        } else if (lowercase === "jinshui") {
            this.engine = new JinShui();
        } else if (lowercase === "shuikong") {
            this.engine = new ShuiKong();
        } else if (lowercase === 'jinhang') {
            this.engine = new JinHang();
        }
    }
    fpkj = (url, data):Promise<any> =>{
        return this.engine.fpkj(url, data);
    }
    fpzf = (url, data) => {
        this.engine.fpzf(url, data);
    }
    fpdy = (url, data) => {
        this.engine.fpdy(url, data);
    }
}